'use strict';

// MODULES //

var tape = require( 'tape' );
var toAccessorArray = require( '@stdlib/array/base/to-accessor-array' );
var cuany = require( './../lib/assign.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof cuany, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function cumulatively tests and assigns to array (generic)', function test( t ) {
	var expected;
	var actual;
	var x;
	var y;

	x = [ false, false, true, false, false ];
	y = [ false, null, false, null, false, null, false, null, false, null ];

	actual = cuany( x, y, 2, 0 );
	expected = [ false, null, false, null, true, null, true, null, true, null ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ true, false, true, false, true ];
	y = [ false, true, false, true, false ];

	actual = cuany( x, y, 1, 0 );
	expected = [ true, true, true, true, true ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ false, false, false, false, false ];
	y = [ false, false, true, false, false ];

	actual = cuany( x, y, 3, 0 );
	expected = [ false, false, true, false, false ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ false, false, false, false, false, false, true, false, false, false ];
	y = [ false, false, true, false, false ];

	actual = cuany( x, y, 1, 0 );
	expected = [ false, false, false, false, false ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [];
	y = [ false, false, true, false, false ];

	actual = cuany( x, y, 1, 0 );
	expected = [ false, false, true, false, false ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ true ];
	y = [ false ];

	actual = cuany( x, y, 1, 1 );
	expected = [ false ];

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function cumulatively tests and assigns to array  (accessor)', function test( t ) {
	var expected;
	var actual;
	var x;
	var y;

	x = toAccessorArray( [ false, false, true, false, false ] );
	y = toAccessorArray( [ false, null, false, null, false, null, false, null, false, null ] );

	actual = cuany( x, y, 2, 0 );
	expected = toAccessorArray( [ false, null, false, null, true, null, true, null, true, null ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ true, false, true, false, true ] );
	y = toAccessorArray( [ false, true, false, true, false ] );

	actual = cuany( x, y, 1, 0 );
	expected = toAccessorArray( [ true, true, true, true, true ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ false, false, false, false, false ] );
	y = toAccessorArray( [ false, false, true, false, false ] );

	actual = cuany( x, y, 3, 0 );
	expected = toAccessorArray( [ false, false, true, false, false ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ false, false, false, false, false, false, true, false, false, false ] );
	y = toAccessorArray( [ false, false, true, false, false ] );

	actual = cuany( x, y, 1, 0 );
	expected = toAccessorArray( [ false, false, false, false, false ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [] );
	y = toAccessorArray( [ false, false, true, false, false ] );

	actual = cuany( x, y, 1, 0 );
	expected = toAccessorArray( [ false, false, true, false, false ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ true ] );
	y = toAccessorArray( [ false ] );

	actual = cuany( x, y, 1, 1 );
	expected = toAccessorArray( [ false ] );

	t.strictEqual( actual, y, 'returns the same array instance' );
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();
});
